import axios from 'axios'

export default class RS extends React.Component{
	constructor(props){
		super(props)
		this.state={
			rate:"",
			review:"",
			bid:this.props.uid,
			lid:this.props.itm._id,
			// rated:this.props.itm.rated
		}
 		this.onSubmit = this.onSubmit.bind(this);
        this.onChangeQ = this.onChangeQ.bind(this);

	}
   onSubmit(e){
		e.preventDefault()

		axios.post('http://localhost:4000/addrate',this.state)
             .then(res => {
                 window.location.reload()
             })
             .catch(function(error) {
                 console.log(error);
             })
	}




	onChangeQ(event){

        this.setState({ review: event.target.value })

	}

	render(){
		var left=this.props.itm.amount-this.props.itm.buyed
		var pisa =  this.props.itm.price/this.props.itm.amount
		var hide="display"
		var hide1="none"
		var rev=""
		var rat;
		if(this.props.itm.rated==1){
			hide="none"
			hide1="display"
			var lol=this.props.itm.buyers
			for(var i in lol){
				if(lol[i].bid==uid){

					rev=lol[i].review
					rat=lol[i].rating
				}
			}
		}

		return(
  		<div>


        <a  style={{margin:'5px'}} className="list-group-item list-group-item-action flex-column align-items-start">
   			 <div className="d-flex w-100 justify-content-between">
    		  <h5 className="mb-1">{this.props.itm.vname}</h5>
   			 </div>
   			 <p className="mb-1">{this.props.itm.listname}</p>

   			 <form style={{display:hide}} onSubmit={this.onSubmit}>
                    <div className="form-group">        
                        <input type="text" 
                               className="form-control" 
                               value={this.state.review}
                               onChange={this.onChangeQ}
                               />
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Add Review" className="btn btn-primary"/>
                    </div>

              </form>
              <p style={{display:hide1}}>
              {rev}
              {rat}
              </p>


        </a>
        </div>
		);
  }
}
