import React from 'react'
import axios from 'axios'
export default class List extends React.Component{
	constructor(props){
		super(props) 
        this.onClick = this.onClick.bind(this);

	}
    componentDidMount(){
        var mode = this.props.type

        if (mode=="all") {
            var elements = document.getElementsByClassName("btn btn-info hide-me")

        for (var i = 0; i < elements.length; i++){
            elements[i].style.display = "none";
            }
        }
    }
    onClick(e) {
        e.preventDefault();


        axios.post('http://localhost:4000/dispatch', {id:this.props.itm._id})
                 .then(res => {
                   if(res.data.success==true)
                   {
                      document.getElementById("afterdis").innerHTML = "dispatched";

                   }
                   else{
                      document.getElementById("afterdis").innerHTML = "F";
                   }
                 });
    }

	render(){
        console.log(this.props)
		return(
  		<div>

        <li className="list-group-item d-flex justify-content-between align-items-center">
        {this.props.itm.listname}
        <span className="badge badge-primary badge-pill">{this.props.itm.buyed}/{this.props.itm.amount}</span>
        <button type="button"  class="btn btn-info hide-me" onClick={this.onClick}>dispatch</button>
        <span id="afterdis"></span>
        </li>

        </div>
		);
  }
}
