        <Route path="/dashboard/dispatched" component={() => <Dashboard />}/>
        <Route path="/dashboard/search" component={() => <Dashboard />}/>
        <Route path="/dashboard/track" component={() => <Dashboard />}/>
        <Route path="/dashboard/rate" component={() => <Dashboard />}/>*/}

		</Router>
		);
	}
}
