import React from "react"
import axios from 'axios';

export default class Dashboard extends React.Component{
	constructor(){
		super()
		var st=localStorage.getItem('mydata')
		console.log(st)

		if (st==null)
		{
			window.location.assign('/user/login');
		}

		this.state=JSON.parse(st)
        this.onClick=this.onClick.bind(this);

	}
	onClick(){
		localStorage.clear()
		window.location.assign('/')
	}
	render(){
		return(
			<div>

			<nav className="navbar navbar-expand-lg navbar-light bg-light">
	          <Link to="/" className="navbar-brand">App</Link>
	          <div className="collapse navbar-collapse">
	            <ul className="navbar-nav mr-auto">
	              <li className="navbar-item">
	                <Link to="/vendor" className="nav-link">Vendor Login</Link>
	              </li>
	              <li className="navbar-item">
	                <Link to="/user" className="nav-link">User-Login</Link>
	              </li>
	            </ul>
	          </div>
	       </nav>

				<button id="lolout" className="btn btn-primary" onClick={this.onClick}>LOGOUT</button>

			</div>
			);
	}
}
