import React from 'react'
import axios from 'axios'
export default class Add extends React.Component{
	constructor(props){
		super(props)
		this.onChangeListname= this.onChangeListname.bind(this);
        this.onChangeDes = this.onChangeDes.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeAmount=this.onChangeAmount.bind(this);
        this.onChangePrice=this.onChangePrice.bind(this);

        this.state={
        	vid:this.props.id,
        	listname:"",
        	description:"",
        	amount:"",
        	price:""
        }
	}
 	onChangeListname(event) {
        this.setState({ listname: event.target.value });
    }

    onChangeDes(event) {
        this.setState({ description: event.target.value });
    }
    onChangeAmount(event) {
        this.setState({ amount: event.target.value });
    }
    onChangePrice(event) {
        this.setState({ price: event.target.value });
    }
    onSubmit(e) {
        e.preventDefault();
        const List= this.state

        axios.post('http://localhost:4000/add', List)
                 .then(res => {
                   if(res.data.success==true)
                   {
                      document.getElementById("AfterSubmit").innerHTML = "LIST ADDED";

                   }
                   else{
                      document.getElementById("AfterSubmit").innerHTML = "LOGIN FIRST";
                   }
                 });
    }
	render(){


		return(

  			<div>
             <form onSubmit={this.onSubmit}>
                    <div className="form-group">        
                        <label>Name of List </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.listname}
                               onChange={this.onChangeListname}
                               />
                    </div>
                    <div className="form-group">
                        <label>Des: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.description}
                               onChange={this.onChangeDes}

                               />  
                    </div>
                    <div className="form-group">
                        <label>Amount: </label>
                        <input type="number" 
                               className="form-control" 
                               value={this.state.amount}
                               onChange={this.onChangeAmount}
                               />  
                    </div>
                    <div className="form-group">
                        <label>Price: </label>
                        <input type="number" 
                               className="form-control" 
                               value={this.state.price}
                               onChange={this.onChangePrice}
                               />  
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Submit List" className="btn btn-primary"/>
                    </div>
                    <div id ="AfterSubmit">

                    </div>
                </form>
            </div>

		)

	}
}
