	constructor(props){
		super(props)
   }

  componentDidMount() {
        axios.get('http://localhost:4000/getlist')
             .then(response => {
                 this.setState(response.data);
             })
             .catch(function(error) {
                 console.log(error);
             })

  }
	render(){
    var uid=this.props.id
    var uname=this.props.name
    var lol=this.state
    var lis=[]
    var t=1
    // var t=this.state.sort;
