          for (var j in lol[i].buyers){
            if(lol[i].buyers[j].bid==uid){
              f=1
            }
          }
          if(f==0){
          var s=lol[i].amount - lol[i].buyed
          var l=[s,lol[i]]
          lis.push(l)
        }
        }

      }
    }
    }
    if(t==1){  //price
      for (var i in lol){
        if(lol[i].amount!=lol[i].buyed)
        {
        console.log(lol[i])
