        if(lol[i].state==0){

          for (var j in lol[i].buyers){
            if(lol[i].buyers[j].bid==uid){
              f=1
            }
          }
          if(f==0){
            // console.log(lol[i])
            var s=lol[i].price/lol[i].amount
           var l=[s,lol[i]]
            lis.push(l)
          }

        }
      }
    }
    }
    if(t==2){  //rating
      if(lol[i].amount!=lol[i].buyed)
        {
      for (var i in lol){
        var f=0
        if(lol[i].state==0){
        for (var j in lol[i].buyers){
            if(lol[i].buyers[j].bid==uid){
              f=1
            }
          }
          if(f==0){
          var l=[s,lol[i]]
          lis.push(l)
        }
         } 
      }
    }
    }

    lis = lis.sort(function(a,b) {
    return a[0] - b[0];
});
  var li=[]
  for (var i=0;i<lis.length;i++){
    li.push(lis[i][1])
  }
